import { useState } from "react";

const Login = () =>{
  const [email, setEmail]       = useState('11');
  const [password, setPassword] = useState('11');
  const handleLogin = (e) => {
      e.preventDefault();
      fetch('/api/post', {
          method: "POST",
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ email, password }),
        })
          .then((response) => {
            console.log(email, password);
            console.log("Response status:", response.status);
            return response.json();
          })//response.json())
          .then((data) => {
            // If the login was successful, redirect to the home page.
            if (data.success) {
              console.log("TEST SUCCESS")
              //<PopupMessage />
            } else {
              console.log("TEST FAIL")
              //<PopupMessage />
            }
        });
      };

    return(
        <form onSubmit={handleLogin}>
            <label for="fname">Email</label><br></br>
            <input type="text" id="fname" name="email" onChange={(e) => setEmail(e.target.value)}></input><br></br>
            <label for="lname">Password</label><br></br>
            <input type="text" id="lname" name="password" onChange={(e) => setPassword(e.target.value)}></input><br></br>
            <button>Submit</button>
            <p>{email}</p>
            <p>{password}</p>
        </form>
    )        
}
export default Login;
