import React,{useState, useEffect} from 'react';
import Axios from 'axios'
import '../App.css'
import { Link, useParams } from 'react-router-dom';

function UpdatePost() {
    const [postList,setPostList]=useState([]);
    const {id} = useParams();
useEffect(()=>{
    Axios.get(`http://localhost:3002/api/read/${id}`).then((data)=>{
    setPostList(data.data)
    });
    },[])

    return (
        <div className="CreatePost">
            <table>
                <tr>
                    <th>Name</th>
                    <th>Password</th>
                    <th>Update operations</th>
                </tr>
                {postList.map((val,key)=>(
                    <tr>
                        <td><input value={val.name}></input></td>
                        <td><input value={val.password}></input></td>
                        <td><Link to='/'><button onClick={handleClick}>Update record</button></Link></td>
                    </tr>
                ))}
            </table>
        </div>
    )}

export default UpdatePost
