// Declaring a variable that links to lib 'mysql'
var mysql = require('mysql');

//Declaring a variable which stalls the connection details to the mySQL server
var con = mysql.createConnection({
  host: "localhost",
  user: "root",
  password: "password",
  database: "myfoodchoice"
});

//connect to mysql using the connect function
con.connect(function(err) {
  if (err) throw err;
  console.log("Connected!");

  //following part is optional, it is used to test for data output from mySQL side
  var sql = 'SELECT * FROM systemadmin';
  con.query(sql, function (err, result) {
    if (err) throw err;
    console.log(result);
    console.log("Now showing format in key value:");
    console.log(JSON.stringify(result));
  });
});
module.exports = con;
