import React, { useState } from 'react';
import Axios from 'axios';
import '../App.css';
import { useNavigate } from 'react-router';

function CreatePost() {
  const [email, setEmail] = useState("");
  const navigate = useNavigate();

  const submitPost = () => {
    Axios.delete('http://localhost:3002/api/delete', { data: { email: email } })
      .then(response => {
        // Handle success, e.g., show a success message and navigate
         // Assuming the server sends a message upon success
        navigate('/');
      })
      .catch(error => {
        // Handle error, e.g., show an error message
        console.error(error);
      });
  }

  return (
    <div className="CreatePost">
      <div className="uploadPost">
        <label>Email: </label>
        <input type="text" onChange={(e) => {
          setEmail(e.target.value)
        }} />
        <button onClick={submitPost}>Delete account</button>
      </div>
    </div>
  );
}

export default CreatePost;
