import { useState } from "react";
const Login = () =>{
    const [email, setEmail]       = useState('');
    const [password, setPassword] = useState('');
    const handleLogin = (e) => {
        e.preventDefault();
        const con = mysql.createConnection({
            host: "localhost",
            user: "root",
            password: "password",
            database: "myfoodchoice"
          });

        const query = "SELECT email, password FROM systemadmin WHERE email = ? AND hash = ?";
        con.query(query, [username, password], (error, results) => {
            if(error){
                console.log(error);
            }
            else{
                console.log(results);
                const account = results[0];
                if (account.email === email){
                    console.log("success");
                }
                else{
                    console.log("wrong password");
                }
            }
        });
       
    
      };

      
    return(
        <form onSubmit={handleLogin}>
            <label for="fname">Email</label><br></br>
            <input type="text" id="fname" name="fname" onChange={(e) => setEmail(e.target.value)}></input><br></br>
            <label for="lname">Password</label><br></br>
            <input type="text" id="lname" name="lname" onChange={(e) => setPassword(e.target.value)}></input><br></br>
            <button>Submit</button>
            <p>{email}</p>
        </form>
    )        
}
export default Login;
