import React,{useState,useEffect} from 'react'
import Axios from 'axios'
import '../App.css'
import { Link } from 'react-router-dom';

function MainPage() {
  const [postList,setPostList]=useState([]);

    useEffect(()=>{
    Axios.get("http://localhost:3002/api/get").then((data)=>{
    setPostList(data.data)
    });
    },[]) 
    function handleClick(event, id){
        event.preventDefault()
        console.log(id);
        Axios.delete(`http://localhost:3002/api/delete/${id}`)
        .then(res => {  
            console.log(res);  
            console.log(res.data);  
        })
    }
return (
  <div>
  <table>
      <tr>
          <th>ID</th>
          <th>Email</th>
          <th>Name</th>
          <th>Password</th>
          <th>Gender</th>
          <th>AccountType</th>
          <th>Read Operations</th>
          <th>Update Operations</th>
      </tr>
      {postList.map((val,key)=>(
          <tr>
              <td>{val.id}</td>
              <td>{val.email}</td>
              <td>{val.name}</td>
              <td>{val.password}</td>
              <td>{val.gender}</td>
              <td>{val.accountType}</td>
              <td><Link to={`/readpost/${val.id}`}><button>Read</button></Link></td>
              <td><Link to={`/updatepost/${val.id}`}><button>Update</button></Link></td>
              <td><button onClick={handleClick(val.id)}>Delete</button></td>
          </tr>
      ))}
      </table>
  </div>
    )}
export default MainPage
