const getConnection = require("./db")

const addUser = async (email, name, password, gender, acctype) => {
  const connection = await getConnection()
  return connection.execute(
    "INSERT INTO EMPLOYEE (email, name, password, gender, accountType) values (?,?,?,?,?)",
    [email, name, password, gender, acctype]
  )
}

const getUser = async id => {
    const connection = await getConnection()
    return connection.execute(
      "select * from employee;"
    )
  }

module.exports = {
  addUser,
  getUser
}
