import React, { useState, useEffect } from 'react';
import Axios from 'axios';
import { useNavigate } from 'react-router';

function UpdatePost() {
  const [formValues, setFormValues] = useState({
    email: '',
    name: '',
    password: '',
    gender: '',
    accounttype: '',
  });
  const navigate = useNavigate();

  // onSubmit handler
  const submitPost = () => {
    Axios.put(`http://localhost:3002/api/update`, formValues)
      .then((res) => {
        if (res.status === 200) {
          alert('Account successfully updated');
          navigate('/');
        } else {
          Promise.reject();
        }
      })
      .catch((err) => alert('Something went wrong'));
  };

  // useEffect to load data
  useEffect(() => {
    // Replace the following line with the logic to fetch the user's data by their ID
    const userId = 1; // Replace with the actual user ID you want to update
    Axios.get(`http://localhost:3002/api/user/${userId}`)
      .then((res) => {
        const { email, name, password, gender, accounttype } = res.data;
        setFormValues({ email, name, password, gender, accounttype });
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div className="UpdatePost">
      <label>Email: </label>
      <input
        type="text"
        value={formValues.email}
        onChange={(e) => setFormValues({ ...formValues, email: e.target.value })}
      />
      <label>Name: </label>
      <input
        type="text"
        value={formValues.name}
        onChange={(e) => setFormValues({ ...formValues, name: e.target.value })}
      />
      <label>Password: </label>
      <input
        type="text"
        value={formValues.password}
        onChange={(e) =>
          setFormValues({ ...formValues, password: e.target.value })
        }
      />
      <label>Gender: </label>
      <input
        type="text"
        value={formValues.gender}
        onChange={(e) =>
          setFormValues({ ...formValues, gender: e.target.value })
        }
      />
      <label>Account Type: </label>
      <input
        type="text"
        value={formValues.accounttype}
        onChange={(e) =>
          setFormValues({ ...formValues, accounttype: e.target.value })
        }
      />
      <button onClick={submitPost}>Update account</button>
    </div>
  );
}

export default UpdatePost;
