import React from 'react';
import Navbar from './Navbar';

const Loyalty = () => {
  // Define a static array of loyalty items
  const loyaltyItems = [
    {
      id: 1,
      name: 'Item 1',
      description: 'Description for Item 1',
      points: 100,
      image: 'Fairprice10.jpg',
    },
    {
      id: 2,
      name: 'Item 2',
      description: 'Description for Item 2',
      points: 200,
      image: 'Fairprice20.jpg',
    },
    {
      id: 3,
      name: 'Item 3',
      description: 'Description for Item 3',
      points: 300,
      image: 'item3.jpg',
    },
  ];

  return (
    <div>
      <Navbar />
      <div className="divcss">
        <h2>Loyalty Items</h2>

        <ul>
          {loyaltyItems.map((item) => (
            <li key={item.id}>
              <h3>{item.name}</h3>
              {item.image && <img src={item.image} alt={item.name} />} {/* Display image if available */}
              <p>Description: {item.description}</p>
              <p>Points required: {item.points}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Loyalty;
