import React, { useState } from 'react';
import Axios from 'axios';
import '../App.css';
import { useNavigate } from 'react-router';

function SignUp() {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [gender, setGender] = useState('');
  const [accounttype, setAccountType] = useState('');
  const [existingEmail, setExistingEmail] = useState('');
  const [existingName, setExistingName] = useState('');
  const navigate = useNavigate();

  const checkExistingData = () => {
    // Check if the email or name matches existing data
    if (existingEmail === email) {
      alert('Email already exists. Please use a different email.');
    } else if (existingName === name) {
      alert('Name already exists. Please use a different name.');
    } else {
      // If no matches found, proceed with registration
      submitPost();
    }
  };

  const submitPost = async () => {
    try {
      const response = await Axios.post('http://localhost:3002/api/signup', {
        email: email,
        name: name,
        password: password,
        gender: gender,
        accounttype: accounttype,
      });

      if (response.status === 200) {
        alert('Sign-up successful!');
        navigate('/loginpage');
      } else {
        alert('Sign-up failed. Please try again.');
      }
    } catch (error) {
      console.error('Error creating user:', error);
      alert('Error creating user. Please try again.');
    }
  };

  return (
    <div className="SignUp">
      <div className="uploadAcc">
        <div>
          <label>Email: </label>
          <input
            type="text"
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
        </div>
        <div>
          <label>Name: </label>
          <input
            type="text"
            onChange={(e) => {
              setName(e.target.value);
            }}
          />
        </div>
        <div>
          <label>Password: </label>
          <input
            type="password"
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
        </div>
        <div>
          <label>Gender: </label>
          <select
            value={gender}
            onChange={(e) => {
              setGender(e.target.value);
            }}
          >
            <option value="">Select Gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
        </div>
        <div>
          <label>Account Type: </label>
          <select
            value={accounttype}
            onChange={(e) => {
              setAccountType(e.target.value);
            }}
          >
            <option value="">Select Account Type</option>
            <option value="User">User</option>
            <option value="System Admin">System Admin</option>
            <option value="Product Owner">Product Owner</option>
          </select>
        </div>
        <button onClick={checkExistingData}>Sign Up</button>
      </div>
    </div>
  );
}

export default SignUp;
