import { useState } from "react";
const Login = () =>{
    const [email, setEmail]       = useState('');
    const [password, setPassword] = useState('');
    const [userCred, setuserCred] = useState('');
    const handleLogin = (e) => {
        e.preventDefault();
        fetch('/test.js')
        .then(res => res.json())
        .then(data =>{
            setuserCred(data)
            if (userCred.email === email){
                console.log('Login successful');
            }
            else {
                console.log('Login Failure');
            }
        });
        };
       
      
    return(
        <form onSubmit={handleLogin}>
            <label for="fname">Email</label><br></br>
            <input type="text" id="fname" name="fname" onChange={(e) => setEmail(e.target.value)}></input><br></br>
            <label for="lname">Password</label><br></br>
            <input type="text" id="lname" name="lname" onChange={(e) => setPassword(e.target.value)}></input><br></br>
            <button>Submit</button>
            <p>{email}</p>
        </form>
    )        
}
export default Login;
